mapper.select:
select *
from t_file_object_share
where create_user = #{uid}
  and is_delete = 0
  and file_object_id = #{fid}
limit 1

select s.*, f.file_name, f.file_size, bk.buckets_name from t_file_object_share as s join t_file_object as f on f.id = s.file_object_id join t_buckets as bk on bk.id = f.buckets_id
and s.is_delete = 0
and s.create_user = #{uid}
and bk.id = #{bucketsId}
and f.file_name like concat('%',#{fileName},'%')
